import 'package:flutter/material.dart';import 'package:get/get_rx/get_rx.dart';import 'package:get/get_state_manager/src/rx_flutter/rx_obx_widget.dart';import 'package:prueba1/second_route.dart';import 'package:prueba1/third_page.dart';List<String> fruits = ["Pera", "Manzana", "Melon", "Sandia", "Platano", "Naranja", "Zanahoria", "Aguacate"].obs;class FirstRoute extends StatelessWidget {  const FirstRoute({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: const Color(0xffD4A7C2),      appBar: AppBar(title: const Text("Prueba 1")),      body: Column(        children: [          Obx(            () => SizedBox(              height: 480,              width: 480,              child: listFruits(),            ),          ),          sortBottons(),          aplicationBottons(context),        ],      ),    );  }  Widget sortBottons() {    return Row(      mainAxisAlignment: MainAxisAlignment.center,      children: [        Align(          alignment: Alignment.topLeft,          child: Container(            width: 165.0,            height: 60.0,            decoration: BoxDecoration(              borderRadius: BorderRadius.circular(20.0),              color: Color(0xffA7CFD4),              boxShadow: const [                BoxShadow(                  blurRadius: 15.0,                  offset: Offset(0.0, 0.75),                ),              ],            ),            child: SizedBox(                width: 200.0,                height: 375.0,                child: Row(                  mainAxisAlignment: MainAxisAlignment.spaceBetween,                  children: [                    TextButton(                      onPressed: () {                        sortAsc(fruits);                      },                      child: const Text("Ordenar ASC"),                    ),                    const Icon(                      Icons.arrow_upward,                      color: Colors.black,                      size: 30.0,                    ),                  ],                )),          ),        ),        Align(            alignment: Alignment.topLeft,            child: Padding(              padding: const EdgeInsets.all(12.0),              child: Container(                width: 165.0,                height: 60.0,                decoration: BoxDecoration(                  borderRadius: BorderRadius.circular(20.0),                  color: Color(0xffA7CFD4),                  boxShadow: const [                    BoxShadow(                      blurRadius: 5.0,                      offset: Offset(0.0, 0.75),                    ),                  ],                ),                child: SizedBox(                  width: 200.0,                  height: 375.0,                  child: Row(                    mainAxisAlignment: MainAxisAlignment.spaceBetween,                    children: [                      TextButton(                        onPressed: () {                          sortDesc(fruits);                        },                        child: const Text("Ordenar DESC"),                      ),                        const Icon(                          Icons.arrow_downward,                          color: Colors.black,                          size: 30.0,                        ),                    ],                  ),                ),              ),            )),      ],    );  }  Widget aplicationBottons(BuildContext context) {    return Row(      mainAxisAlignment: MainAxisAlignment.center,      children: [        Align(          alignment: Alignment.topLeft,          child: Container(            width: 165.0,            height: 60.0,            decoration: BoxDecoration(              borderRadius: BorderRadius.circular(20.0),              color: Color(0xffA7CFD4),              boxShadow: const [                BoxShadow(                  blurRadius: 5.0,                  offset: Offset(0.0, 0.75),                ),              ],            ),            child: TextButton(              onPressed: () {                Navigator.push(                  context,                  MaterialPageRoute(builder: (context) => SecondRoute()),                );              },              child: const Text("Ver fotos"),            ),          ),        ),        Align(            alignment: Alignment.topRight,            child: Padding(              padding: const EdgeInsets.all(12.0),              child: Container(                width: 165.0,                height: 60.0,                decoration: BoxDecoration(                  borderRadius: BorderRadius.circular(20.0),                  color: Color(0xffA7CFD4),                  boxShadow: const [                    BoxShadow(                      blurRadius: 5.0,                      offset: Offset(0.0, 0.75),                    ),                  ],                ),                child: TextButton(                  onPressed: () {                    Navigator.push(                      context,                      MaterialPageRoute(builder: (context) => ThirdPage()),                    );                  },                  child: const Text("Ver animaci√≥n"),                ),              ),            )),      ],    );  }  Widget listFruits() {    return ListView.builder(        itemCount: fruits.length,        itemBuilder: (BuildContext context, int index) {          return ListTile(            title: Center(              child: Text(                fruits.elementAt(index),                style: const TextStyle(fontWeight: FontWeight.bold, fontSize: 20),              ),            ),          );        });  }  void sortAsc(List<String> array) {    array.sort((a, b) {      return a.compareTo(b);    });  }  void sortDesc(List<String> array) {    array.sort((a, b) {      return b.compareTo(a);    });  }}